import React, { Component } from 'react'



// use this props and call just the nom and prenom;
class Bonj extends Component {

    render(){
        const {nom, prenom} = this.props;
        return(
            <div>
        <h1>Bounjour {prenom} {nom}</h1>
    </div>
        )
        
        
    }
    
    
}
export default Bonj;

// props for print name and last name;
export function Hello(props) {
     return(
         <h1>Hello, mr {props.prenom} {props.nom}</h1>
     )
 }

 export class Bon extends Component {
    constructor(props){
        super(props);
        this.state = {Name: 'Ali'}
    }
    changeName = () => {
            this.setState({Name: 'Houssam'})
    }
    changeName2 = () => {
            this.setState({Name: 'Ali'})
    }
    render(){
        return(
            <div>
                <h1>Bounjour {this.state.Name}</h1>
                <button className='ml-2' type='button' onClick={this.changeName}>Change Name</button>
                <button className='ml-2' type='button' onClick={this.changeName2}>Rest Name</button>
            </div>
        )
    }
 }
