import React, {Component} from 'react'


class Inscri extends Component {
    constructor(){
        super()
       this.state = {       
        renumber:0,
        number:0,
        col: 'red',
        

        
       }

      }
    incriment (){
      this.setState({renumber: this.state.renumber +=2} )
      
    }
    renicialise(){
      this.setState({renumber: this.state.renumber = 0} )
    }

    RandomUp(){
      this.setState({number: this.state.number +   Math.trunc(Math.random()*10)+1 })
    }
    RandomDown(){
      this.setState({number: this.state.number -   Math.trunc(Math.random()*10+1), col:this.number >= 50 ? this.state.col : 'green' })
    }
    
    render() {
     
        return (
      <div>
        
       <h1>1 valuer du compteur : {this.state.renumber}</h1>
       <span>
        <button onClick={()=>this.incriment()}>incriment</button>       
        <button onClick={()=>this.renicialise()}> Renicialer</button>       
        </span>

         
       <h1 style={{backgoundColor: this.state.number >= 50 ? 'red': this.state.col}}>2 valuer du compteur : {this.state.number}</h1>
       <span>
        <button onClick={()=>this.RandomUp()}>Up</button>       
        <button onClick={()=>this.RandomDown()}> Down</button>       
        </span>



       {/* <li>Affiche la valuer du compteur : </li>
       <li>Affiche la valuer du compteur : </li> */}
        
      </div>
    )
  }

}
export default Inscri
