import React from 'react';

class PigGame extends Component {
  // Set the initial state of the game
  state = {
    player1Score: 0,
    player2Score: 0,
    currentPlayer: 1,
    roll: 0
  };

  // Roll the dice and update the state
  rollDice = () => {
    const roll = Math.floor(Math.random() * 6) + 1;
    this.setState({ roll });

    // If the roll is 1, switch players and reset the roll to 0
    if (roll === 1) {
      this.setState({
        currentPlayer: this.state.currentPlayer === 1 ? 2 : 1,
        roll: 0
      });
    }
  };

  // Add the roll to the current player's score
  addToScore = () => {
    if (this.state.currentPlayer === 1) {
      this.setState(prevState => ({
        player1Score: prevState.player1Score + this.state.roll
      }));
    } else {
      this.setState(prevState => ({
        player2Score: prevState.player2Score + this.state.roll
      }));
    }
  };

  // Reset the game to its initial state
  resetGame = () => {
    this.setState({
      player1Score: 0,
      player2Score: 0,
      currentPlayer: 1,
      roll: 0
    });
  };

  render() {
    return (
      <div>
        <h1>Pig Game</h1>
        <div>
          <p>Player 1 Score: {this.state.player1Score}</p>
          <p>Player 2 Score: {this.state.player2Score}</p>
        </div>
        <div>
          <p>Current Player: {this.state.currentPlayer}</p>
          <p>Roll: {this.state.roll}</p>
        </div>
        <button onClick={this.rollDice}>Roll Dice</button>
        <button onClick={this.addToScore}>Add to Score</button>
        <button onClick={this.resetGame}>Reset Game</button>
      </div>
    );
  }
}

export default PigGame
