import { Component } from "react";

class JeuDe extends Component {
  constructor(props) {
    super(props);
    this.state = { face: null, compteur: 0, end: false };
  }
  Play = () => {
    let facealea = Math.floor(Math.random() * 6 + 1);
    this.setState({ face: facealea, compteur: this.state.compteur + 1 });
    if (facealea === this.props.cache) this.setState({ end: true });
  };
  Rest = () => {
    this.setState({ face: null, compteur: 0, end: false });
  };
  render() {
    return (
      <div className="container pt-5  d-flex justify-content-center">
            <div className="card col-4  pb-5 ">
                <div className="card-header ">
                  <img
                    style={{maxWidth:'100%'}}
                      src={
                        this.state.face == null
                          ? "images/all.jpg"
                          : `images/${this.state.face}.jpg`
                      } alt="tst"
                  />
                </div>
        <h1>Jeu de Dé...</h1>
            <div className=" border rounded bg-dark text-white ">
              <p>face : {this.state.face}</p>
              <p>Nombre d'essais : {this.state.compteur}</p>
            </div>
                {this.state.end ? (
            <div>
                <p>Bravo! Vous avez trouvez la face cachée</p>
              </div>
            ) : (
              <div >
                <hr></hr>
              <button className="btn btn-outline-dark mr-2" onClick={this.Play}>Play</button>
              <button className="btn btn-outline-dark " onClick={this.Rest}>Rest</button>
              </div>
            )}
          </div>
      </div>
    );
  }
}
export default JeuDe;
