import React, { useState } from 'react'

function Form(){

    const [pass, setpass] = useState('')
  
    const [info, setinfo] = useState('')
   
function handleAfficher(){
    setinfo(`password : ${pass} repassword : ${pass}`)
    if(setpass.length < 4 ){
        setinfo(`Password doit avo`)
    }else{
        setinfo()
    }
} 





    return(
       <div >
        <div>
            <label> Entrer Votre password  : </label>
            <input type='password' onChange={(event)=>setpass(event.target.value.toUpperCase())}  />
            <p>{info}</p>
        </div>
        <div>
        <label> Entrer Votre password  : </label>
            <input type='password' onChange={(event)=>setpass(event.target.value.toUpperCase())}  />
        </div>
           
            <button className='btn btn-outline-primary' onClick={()=>handleAfficher()}>Afficher</button>

            
           
        </div>
        
    )
}

export default Form
