import { Component } from "react";

class JeuDe extends Component {
  constructor() {
    super();
    this.state = { face: null, compteur: 0, fin: false };
  }
  Play = () => {
    let roll = Math.floor(Math.random() * 6 + 1);
    this.setState({ face: roll, compteur: this.state.compteur + 1 });
    if (roll === this.props.cache) this.setState({ fin: true });
  };
  Rest = () => {
    this.setState({ face: null, compteur: 0, fin: false });
  };
  render() {
    return (
      <div className=" pt-2  d-flex justify-content-center">
        <div className="border border-3 border-dark card col-5  pb-5 ">
          <div className="card-header ">
            <img  
              src={
                this.state.face == null
                ? "images/all.jpg"
                : `images/${this.state.face}.jpg`
              } alt="tst"
              style={{ maxWidth: '100%' }}
            />
          </div>
          <h1>Jeu de DÃ©...</h1>
          <div className=" border rounded bg-dark text-white ">
            <p>face : {this.state.face}</p>
            <p>Nombre d'essais : {this.state.compteur}</p>
          </div>
          {/* {this.state.fin ? ( */}
          
            <div >
              <hr/>
                {/* <button className="btn btn-outline-dark mr-2" onClick={(this.state.compteur >= 8 ? this.Rest : this.Play)} >Play</button> */}
                <button className="btn btn-outline-dark mr-2" onClick={ (this.state.face === 1 ? this.Rest : this.Play ?this.Rest: this.Play)} >Play</button>
                <button className="btn btn-outline-dark " onClick={this.Rest}>Rest</button>
              <hr/>
            </div>

            <div className="border outline-white rounded bg-dark text-white">
              <p  style={{color: this.state.face  === 1 ? 'red' : ''}}> {this.state.face === 1 ? 'You Lost The Game ðŸ˜£' : 'Roll Again'}</p>
              <p style={{color: this.state.compteur === 8 ? 'green' : ''}}> {this.state.compteur ===  8 ? 'You Win The Game ðŸŽ‰' : ''}</p>
            </div>  
          
        </div>
      </div>
    );
  }
}
export default JeuDe;
